// Functie om de content schoon te maken en JSON-compliant te maken
function cleanContent(input) {
    return input
        .replace(/<think>[\s\S]*?<\/think>/g, "") // Verwijder alles tussen <think> en </think>, inclusief de tags
        .replace(/[\u0000-\u001F\u007F-\u009F]/g, "") // Verwijder controlekarakters
        .replace(/"/g, '\\"') // Escape dubbele aanhalingstekens
        .replace(/\n/g, " ") // Vervang nieuwe regels door spaties
        .replace(/\s{2,}/g, " ") // Vervang meerdere spaties door een enkele spatie
        .trim(); // Verwijder spaties aan het begin en einde
}

// Haal de content op uit het aangegeven element
const aiResponse = $input.first().json.output || "";

// Maak de content schoon
const cleanedContent = cleanContent(aiResponse);

// Geef alleen de opgeschoonde content door aan de volgende node
return [{ json: { cleanedContent } }];

// ‚îÄ‚îÄ Stap 1: verzamel √©lke afbeelding/clip-url uit ‚ÄúGet Images‚Äù ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
const imgItems = $items("Get Images2Videos");   // g√©√©n tweede parameter ‚áí echte array

const imageList = imgItems
  .flatMap(item => {
    const urls = [];

    // 1) Array van images ‚Üí { url: ... }
    if (Array.isArray(item.json.images)) {
      for (const img of item.json.images) {
        if (img?.url) urls.push(img.url);
      }
    }

    // 2) Losse url op root
    if (item.json.url) {
      urls.push(item.json.url);
    }

    // 3) Geneste video.url  (extra vangnet)
    if (item.json.video?.url) {
      urls.push(item.json.video.url);
    }

    return urls;
  })
  .filter(Boolean);   // verwijder undefined/lege waarden

if (imageList.length === 0) {
  throw new Error('Geen afbeeldingen gevonden in node "Get Images".');
}

// ‚îÄ‚îÄ Stap 2: bouw exact dezelfde output-vorm ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
return [{
  json: {
    type: "ImageVideo",
    data: {
      background_url: $node["Map Music"].json["Music URL"],
      image_list:     imageList,                                // <== ongewijzigd veld
      voice_url:      $('Map Public Link').first().json["Audio URL"],
      transcripts:    $node["Combine Transcript"].json.chunks
    }
