}];

return items.map(item => {
	// ‚îÄ‚îÄ 1. Verzamel alle woord-chunks ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
	const words = item.json.chunks;          // ‚Üê Fal.ai schema
	const result = [];

	if (!Array.isArray(words) || words.length === 0) {
		return { json: { groups: [] } };
	}

	// ‚îÄ‚îÄ 2. Initialiseer de eerste 6-secondenblok ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
	let startTime   = words[0].timestamp[0]; // eerste woord, begin-ms
	let endTime     = startTime + 5;         // blokduur = 6s
	let currentGrp  = [];
	let lastEnd     = words[0].timestamp[1];
	let index       = 0;

	// ‚îÄ‚îÄ 3. Doorloop alle woorden ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
	for (const w of words) {
		const wStart = w.timestamp[0];
		const wEnd   = w.timestamp[1];
		const wText  = (w.text || "").trim();  // spatie v√≥√≥r woord verwijderen

		if (wStart < endTime) {
			currentGrp.push(wText);
			lastEnd = wEnd;                   // houd laatste eind-timestamp bij
		} else {
			// sluit vorige groep af
			result.push({
				text: currentGrp.join(" "),
				start: startTime,
				end:   lastEnd,
				index,
			});

			// start nieuwe groep
			index      += 1;
			startTime   = wStart;
			endTime     = startTime + 5;  // Adjust this and lower it for more images in the video.
			currentGrp  = [wText];
			lastEnd     = wEnd;
		}
	}

	// ‚îÄ‚îÄ 4. Restgroep toevoegen ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
	if (currentGrp.length) {
		result.push({
			text: currentGrp.join(" "),
			start: startTime,
			end:   lastEnd,
			index,
		});
	}

	// ‚îÄ‚îÄ 5. Output in n8n-formaat ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
	return { json: { groups: result } };
});

