// ‚îÄ‚îÄ 1. Ruwe Whisper-chunks ophalen ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
const chunks = $node["Get Fal Whisper Result"].json.chunks ?? [];
if (chunks.length === 0) throw new Error("Geen Whisper-chunks beschikbaar.");

function titleCase(word) {
  return word.charAt(0).toUpperCase() + word.slice(1);
}

// ‚îÄ‚îÄ 2. Groepeer telkens 3 woorden tot √©√©n frase ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
const phrases   = [];
let buffer      = [];             // tijdelijke woorden
let phraseStart = null;           // start-tijd van huidige frase

chunks.forEach((c, i) => {
  // Zet start-tijd zodra buffer leeg is
  if (buffer.length === 0) phraseStart = c.timestamp[0];

  buffer.push(c);                           // voeg woord toe

  const bufferVol = buffer.length === 3;    // 3 woorden?
  const laatste   = i === chunks.length-1;  // einde array?

  if (bufferVol || laatste) {
    const phraseText = buffer
      .map(b => titleCase(b.text.trim()))
      .join(" ");

    const phraseEnd  = buffer[buffer.length-1].timestamp[1];

    phrases.push({
      start: phraseStart,
      end:   phraseEnd,
      words: phraseText,
    });

    buffer = [];                 // reset voor volgende frase
  }
});

// ‚îÄ‚îÄ 3. E√©n n8n-item teruggeven met het nieuwe transcript ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
return [{
  json: {
    chunks: phrases,              // <‚Äî downstream te gebruiken
  },
}];

// Controleer of er items zijn
if (!items || items.length === 0) {
  throw new Error('Geen bestanden gevonden in de bucket!');
}

// Base URL die voor de bestandsnaam moet komen
const baseUrl = "https://storage.googleapis.com/YOURBUCKETNAME/";

// Verzamel alle MP3-bestanden (exclusief bg.mp3) met hun naam en selfLink
const mp3Files = items
  .map(item => ({
    name: item.json.name,       // Naam van het bestand
    url: item.json.selfLink     // Gebruik selfLink in plaats van mediaLink
  }))
  .filter(file => 
    file.name.endsWith('.mp3') && 
    file.name !== 'bg.mp3'      // bg.mp3 uitsluiten
  );

// Gooi een fout als er geen geschikte MP3-bestanden zijn
if (mp3Files.length === 0) {
  throw new Error('Geen geschikte MP3-bestanden gevonden!');
}

// Kies een willekeurig bestand
const randomIndex = Math.floor(Math.random() * mp3Files.length);
const randomFile = mp3Files[randomIndex];

// Definieer de marker in de selfLink URL
const marker = "YOURBUCKETNAME/o/";

// Zoek de positie van de marker in de selfLink
const markerIndex = randomFile.url.indexOf(marker);
if (markerIndex === -1) {
  throw new Error('Marker niet gevonden in de selfLink URL');
}

// Extraheer de bestandsnaam: alles na de marker
let fileName = randomFile.url.substring(markerIndex + marker.length);

// Verwijder eventuele queryparameters (alles na de '?')
const queryIndex = fileName.indexOf("?");
if (queryIndex !== -1) {
  fileName = fileName.substring(0, queryIndex);
}

// Bouw de definitieve URL op door de base URL en de bestandsnaam te combineren
const finalUrl = baseUrl + fileName;

// Retourneer de nieuwe URL in de JSON-respons
return [{ json: { selected_music: finalUrl } }];

