const script = $json.output || $json.text || "";
const words = script.trim().split(/\s+/).length;
const wordsPerSecond = 2;
const estDuration = words / wordsPerSecond;

const maxSeconds = 85;
const tooLong = estDuration > maxSeconds;

return [{
  json: {
    script,
    words,
    estDuration,
    tooLong
  }
}];

// Access the scenes array from the Video Scene Producer output
const producerScenes = $input.first().json.output.scenes;

// Access the music data from the renamed sub-workflow node output
const backgroundMusicUrl = $('Get Next Music Track1').first().json.backgroundMusicUrl;
const backgroundMusicCredits = $('Get Next Music Track1').first().json.backgroundMusicCredits;

// Access the template ID from the 'Set Global Variables1' node
const json2videoTemplateId = $('Set Global Variables').first().json.json2video_template_id;

// Construct the JSON2Video payload (voice parameter removed)
const json2videoPayload = {
  "template": json2videoTemplateId,
  "variables": { // This 'variables' block fills the variables defined within your J2V template
    "scenes": producerScenes, // Your scenes data for the template's 'iterate' variable
    "background_audio_url": backgroundMusicUrl // Pass the actual audio URL here as this variable
  }
};

// Return both the constructed JSON2Video payload AND the background music credits
return [{
  json: {
    json2videoPayload: json2videoPayload, // The payload to send to JSON2Video
    backgroundMusicCredits: backgroundMusicCredits // The credits for later use
  }
}];

