// This is the most fundamental way to get data in n8n.
// It retrieves the array of all incoming items, and we select the first one.
const firstItem = $input.all()[0];

// Now we can safely get the JSON data from that item.
const agentData = firstItem.json;

// This variable will hold our final array of scenes.
let scenesArray = [];

// The rest of this logic is correct and will now work.
if (agentData.output && agentData.output.scenes) {
  scenesArray = agentData.output.scenes;
} else if (agentData.scenes) {
  scenesArray = agentData.scenes;
}

// If we successfully found a scenes array, return it.
if (scenesArray && scenesArray.length > 0) {
  return scenesArray;
} else {
  // If no scenes were found, stop with a clear error.
  throw new Error("Could not find a 'scenes' array in the input data. Check the AI Agent output.");
}

// This node now gets all the data it needs directly from the input.
const allItems = $input.all();

if (!allItems || allItems.length === 0) {
  throw new Error("No items received from the previous node.");
}

// Simply map over the incoming items. All the necessary data
// (text, imagePrompt, imagePath) is already there from the Merge node.
const outputItems = allItems.map((item, index) => {
  return {
    json: {
      text: item.json.text,
      imagePrompt: item.json.imagePrompt,
      sceneIndex: index, // Use the current index
      imagePath: item.json.imagePath,
      imageFilename: item.json.imageFilename,
    }
  };
});

return outputItems;

// N8N Node: "Format JSON2VIDEO Structure"
const allItems = $input.all();

if (!allItems || allItems.length === 0) {
  throw new Error("No items received from Bridge Code1");
}

// Get global variables from n8n (set these in your "Set Global Variables" node)
const globalVars = $('Set Global Variables1').item.json;
const voiceId = globalVars.ELEVENLABS_VOICE_ID || "2ajXGJNYBR0iNHpS4VZb"; // Default voice
const backgroundAudioUrl = globalVars.BACKGROUND_AUDIO_URL || ""; // Optional background music URL

// --- NEW ---
// Get the video title from the global variables node
const videoTitle = globalVars.TITLE || "Untitled_Video";
// --- END NEW ---

console.log(`Formatting ${allItems.length} scenes for JSON2VIDEO clone`);

// Build scenes array in JSON2VIDEO format
const scenes = [];

for (const [index, item] of allItems.entries()) {
  if (!item.json.imagePath) {
    console.warn(`Skipping scene ${index} - no image path`);
    continue;
  }
  scenes.push({
    imageData: item.json.imagePath,
    text: item.json.text,
    voiceOverText: item.json.text,
    imagePrompt: item.json.imagePrompt,
    overlaidText: item.json.text.substring(0, 50) + "...", // Shortened for overlay
    sceneIndex: index
  });
}

// Create JSON2VIDEO-compatible structure
const json2videoConfig = {
  // --- NEW ---
  // Add the title to the JSON object so the Python script can access it
  title: videoTitle,
  // --- END NEW ---
  id: "n8n_generated",
  comment: "Video created by n8n JSON2VIDEO clone",
  height: 1920,
  width: 1080,
  quality: "medium",
  draft: false,
  resolution: "instagram-story",
  fps: 25,
  cache: true,

  scenes: scenes,

  variables: {
    voice: voiceId,
    background_audio_url: backgroundAudioUrl,
    scenes: scenes.map(scene => ({
      overlaidText: scene.overlaidText,
      voiceOverText: scene.voiceOverText,
      imagePrompt: scene.imagePrompt
    }))
  },

  elements: [
    ...(backgroundAudioUrl ? [{
      id: "background-music",
      type: "audio",
      src: backgroundAudioUrl,
      duration: -2,
      "fade-out": 2,
      volume: 0.2
    }] : []),

    {
      id: "subtitles",
      type: "subtitles",
      settings: {
        "font-family": "Oswald Bold",
        "font-size": 140,
        "outline-color": "#000000",
        "outline-width": 8,
        "position": "mid-bottom-center"
      },
      language: "auto",
      comment: "Auto-generated subtitles"
    }
  ]
};

console.log(`JSON2VIDEO structure created with ${scenes.length} scenes`);
console.log(`Title: ${videoTitle}`); // Log the title for debugging
console.log(`Voice ID: ${voiceId}`);
console.log(`Background Audio: ${backgroundAudioUrl || 'None'}`);

// Return the formatted structure
return [{
  json: json2videoConfig
}];

// Get the output from the previous Execute Command node
const executeNodeOutput = items[0].json;
const stdout = executeNodeOutput.stdout;

// Find the starting position of the JSON object
// We use lastIndexOf('{') because the JSON is the last thing printed
const jsonStartIndex = stdout.lastIndexOf('{');

// Check if a JSON object was found
if (jsonStartIndex === -1) {
  throw new Error("Could not find a JSON object in the stdout from the previous node.");
}

// Extract the JSON string from the full stdout
const jsonString = stdout.substring(jsonStartIndex);

try {
  // Parse the extracted string into a usable JavaScript object
  const cleanData = JSON.parse(jsonString);

  // Return the clean data for the next node (e.g., Google Sheets)
  return [{
    json: cleanData
  }];
} catch (error) {
  console.error("Failed to parse JSON string:", jsonString);
  throw new Error("The extracted string could not be parsed as JSON. Check the execute node's output.");
}

const items = $input.all();

for (let i = 0; i < items.length; i++) {
  const item = items[i];

  try {
    // Check different possible paths for the base64 data
    let base64String = null;

    // Try different possible locations for the base64 data
    if (item.json.data && item.json.data.outputs && item.json.data.outputs[0]) {
      base64String = item.json.data.outputs[0];
    } else if (item.json.outputs && item.json.outputs[0]) {
      base64String = item.json.outputs[0];
    } else if (item.json.data && item.json.data.output) {
      base64String = item.json.data.output;
    } else if (item.json.image) {
      base64String = item.json.image;
    } else if (item.json.data && item.json.data.image) {
      base64String = item.json.data.image;
    }

    // Log the structure to debug
    console.log('Item JSON structure:', JSON.stringify(item.json, null, 2));

    if (!base64String) {
      throw new Error(`Base64 string not found. Available keys: ${Object.keys(item.json)}`);
    }

    // Remove data URL prefix if present (e.g., "data:image/jpeg;base64,")
    const cleanBase64 = base64String.replace(/^data:image\/[a-z]+;base64,/, '');

    // Convert Base64 to binary
    const binaryData = Buffer.from(cleanBase64, 'base64');

    // Create binary data object
    const binaryDataObj = await this.helpers.prepareBinaryData(
      binaryData, 
      `generated_image_${Date.now()}.jpg`, 
      'image/jpeg'
    );

    // Set the binary data
    item.binary = {
      data: binaryDataObj
    };

    // Clean up the JSON to save memory
    if (item.json.data && item.json.data.outputs) {
      delete item.json.data.outputs;
    }

  } catch (error) {
    console.error('Error processing item:', error);
    item.json.error = error.message;
  }
}

return items;

// This function creates a unique identifier that looks like a UUID
// without using any external modules. It's random enough for API tracking.
function generateUUID() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

const randomTaskUUID = generateUUID();

return {
  taskUUID: randomTaskUUID
};

// This script splits scenes into smaller chunks based on a maximum word count.

const newItems = [];
// --- You can change this number ---
const maxWords = 10; // Target ~10 words per subtitle chunk

for (const item of $input.all()) {
  const scenes = item.json.output.scenes;
  if (!scenes) {
    newItems.push(item);
    continue;
  }

  const splitScenes = [];
  for (const scene of scenes) {
    const words = scene.text.trim().split(/\s+/); // Split text into words
    let currentChunk = [];

    for (const word of words) {
      currentChunk.push(word);
      if (currentChunk.length >= maxWords) {
        // Create a new scene for this chunk
        splitScenes.push({
          ...scene,
          text: currentChunk.join(' ')
        });
        currentChunk = []; // Reset for the next chunk
      }
    }

    // Add any leftover words as a final scene
    if (currentChunk.length > 0) {
      splitScenes.push({
        ...scene,
        text: currentChunk.join(' ')
      });
    }
  }

  // Replace the original scenes array with our new, smaller scenes
  item.json.output.scenes = splitScenes;
  newItems.push(item);
}

return newItems;

// Modified "Create Temp image file paths" node
const items = $input.all();
const output = [];

// Create a simple hash function for the imagePrompt
function simpleHash(str) {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash; // Convert to 32bit integer
  }
  return Math.abs(hash).toString(36);
}

items.forEach((item, index) => {
  // Use hash of imagePrompt instead of timestamp
  const imageHash = simpleHash(item.json.imagePrompt);
  const filename = `temp_scene_${imageHash}.jpg`;
  const filepath = `/short_video_maker/images/${filename}`;

  output.push({
    json: {
      ...item.json,
      sceneIndex: index,
      imageFilename: filename,
      imagePath: filepath
    },
    binary: item.binary
  });
});

return output;
