// Get the AI's response which contains the final calculated totals
const aiResponse = JSON.parse($input.first().json.choices[0].message.content);
const userMessage = $('Telegram Trigger1').item.json.message;

// Combine the AI response with user and time data
const result = {
  ...aiResponse, // This adds food_name, calories, protein, etc. from the AI
  date: new Date().toISOString().split('T')[0],
  time: new Date().toLocaleTimeString('en-US', {
    hour12: false,
    hour: '2-digit',
    minute: '2-digit',
    timeZone: 'America/Chicago'
  }),
  telegram_user_id: userMessage.from.id,
  telegram_username: userMessage.from.username || userMessage.from.first_name
};

return { json: result };

// Calculate daily totals from Notion results
const notionResults = $input.all();
const currentMeal = $('Calculate Nutrition1').item.json;

let dailyTotals = {
  calories: 0,
  protein: 0,
  fat: 0,
  carbs: 0,
  fiber: 0
};

// Sum up all entries for today
if (notionResults && notionResults.length > 0) {
  notionResults.forEach(item => {
    if (item.json && item.json.properties) {
      const props = item.json.properties;
      dailyTotals.calories += props.Calories?.number || 0;
      dailyTotals.protein += props['Protein (g)']?.number || 0;
      dailyTotals.fat += props['Fat (g)']?.number || 0;
      dailyTotals.carbs += props['Carbs (g)']?.number || 0;
      dailyTotals.fiber += props['Fiber (g)']?.number || 0;
    }
  });
}

// Round the totals
dailyTotals.calories = Math.round(dailyTotals.calories);
dailyTotals.protein = Math.round(dailyTotals.protein * 10) / 10;
dailyTotals.fat = Math.round(dailyTotals.fat * 10) / 10;
dailyTotals.carbs = Math.round(dailyTotals.carbs * 10) / 10;
dailyTotals.fiber = Math.round(dailyTotals.fiber * 10) / 10;

// Create response message
const mealSummary = `ðŸ½ï¸ **Meal Logged: ${currentMeal.food_name}**\n` +
  `ðŸ“Š **This Meal:**\n` +
  `â€¢ Calories: ${currentMeal.calories}\n` +
  `â€¢ Protein: ${currentMeal.protein}g\n` +
  `â€¢ Fat: ${currentMeal.fat}g\n` +
  `â€¢ Carbs: ${currentMeal.carbs}g\n` +
  `â€¢ Fiber: ${currentMeal.fiber}g\n\n` +
  `ðŸ“ˆ **Today's Totals:**\n` +
  `â€¢ Calories: ${dailyTotals.calories}\n` +
  `â€¢ Protein: ${dailyTotals.protein}g\n` +
  `â€¢ Fat: ${dailyTotals.fat}g\n` +
  `â€¢ Carbs: ${dailyTotals.carbs}g\n` +
  `â€¢ Fiber: ${dailyTotals.fiber}g`;

return {
