// Get the original data from before the verification step
const originalData = $('Calculate Nutrition1').item.json;

// Get the verified data from the verifier AI
// Note: The AI Agent node might put the response in a different place than the HTTP node.
// We'll check for a 'content' or direct property.
const aiResponse = $input.first().json;
const verifiedDataString = aiResponse.content || JSON.stringify(aiResponse);
const verifiedData = JSON.parse(verifiedDataString);


// Combine them into the final object for Notion
const result = {
  ...verifiedData, // Use the verified food_name, calories, protein, etc.
  date: originalData.date,
  time: originalData.time,
  telegram_user_id: originalData.telegram_user_id,
  telegram_username: originalData.telegram_username,
};

return { json: result };

// Get the first item from the input array.
// In "Run Once for All Items" mode, 'items' is an array of all input items.
console.log('--- Extract Search Term: Starting execution ---');
console.log('Input items received:', items.length, 'items');

if (!items || items.length === 0) {
    console.error('Error: No input items. Exiting gracefully.');
    return []; // Return an empty array if no input, to prevent crashing on items[0]
}

const currentItem = items[0];
console.log('First item structure (partial):', JSON.stringify(currentItem.json.message));

// Access the message from the currentItem's JSON data
// Add a check to ensure 'text' property exists and is a string
if (!currentItem.json || !currentItem.json.message || typeof currentItem.json.message.text !== 'string') {
    console.error('Error: Message text is missing or not a string. Exiting.');
    return [];
}
const message = currentItem.json.message.text.toLowerCase();
console.log('Original message (lowercase):', message);

// List of keywords to remove from the message to find the search term
const keywords = [
  'my', 'the', 'a', 'an',
  'normal', 'usual', 'typical', 'daily',
  'favorite', 'standard', 'regular',
  'go-to', 'again', 'repeat', 'same as'
];

let searchTerm = message;
console.log('Initial searchTerm:', searchTerm);

for (const keyword of keywords) {
  // This removes the keyword as a whole word
  const regex = new RegExp(`\\b${keyword}\\b`, 'gi');
  console.log(`Applying regex /\\b${keyword}\\b/gi to: "${searchTerm}"`);
  searchTerm = searchTerm.replace(regex, '');
  console.log('searchTerm after keyword removal:', searchTerm);
}

// Clean up any extra spaces left behind and add it to the current item's JSON
// ****** THIS IS THE CRITICAL CHANGE ******
currentItem.json.searchWords = searchTerm.split(' ').filter(word => word.length > 0);
console.log('Final searchTerm added to item.json:', currentItem.json.searchTerm);

// Return the modified array of items
console.log('--- Extract Search Term: Returning items ---');
return items;

const currentItem = items[0];
// Calculate the length of the items array and add it as a new property
currentItem.json.actualItemCount = items.length;

console.log('Code Node "Get Item Count": Calculated actualItemCount as:', currentItem.json.actualItemCount);

return items;

// This node receives all pages from the Notion database.
// It will filter them using the search words from a previous node.

// Get the search words from the first item's JSON
// The items array from the Notion node will be long, so we use the search words
// from the initial trigger, which is available in a different node's output.
const searchWords = $('Extract Search Term').item.json.searchWords;

// Filter the items to only include those where the title contains ALL search words
const filteredItems = items.filter(item => {
  const title = item.json.property_food_item.toLowerCase();
  // 'every' checks if every word in the array matches a condition
  return searchWords.every(word => title.includes(word.toLowerCase()));
});

// Check if any items remain after filtering
if (filteredItems.length === 0) {
  // If no items match, return an empty array to stop the workflow gracefully
  return [];
}

// If there are matches, return the filtered items
return filteredItems;

const options = [];
// Use HTML line breaks
let replyMessage = "Multiple past meals found. Please choose:<br><br>";

// Loop through meals and generate options
items.forEach((item, index) => {
    const mealName = item.json.property_food_item || item.json.name;
    const optionNumber = index + 1;

    options.push({
        optionNumber,
        mealName,
        originalNotionData: item.json
    });

    // Use an HTML line break at the end of each line
    replyMessage += `${optionNumber}. ${mealName}<br>`;
});

// Create the array of button rows
const buttonRows = options.map(option => {
    return [{
        text: option.mealName,
        callback_data: option.optionNumber.toString()
    }];
});

// Create the final, complete keyboard object that the Telegram API expects
const finalKeyboardObject = {
    inline_keyboard: buttonRows
};

// Return all the data needed by the next node
return [{
    json: {
        telegramMessage: replyMessage,
        telegramKeyboardObject: finalKeyboardObject, // The object we'll use
        mealOptions: options
    }
}];

// Get callback data
const callbackData = JSON.parse($json.callback_query.data);
const selectedIndex = callbackData.selectedMealIndex;

// Get stored meal options (from earlier)
const mealOptions = $('Create Message & Keyboard').first().json.mealOptions;
const selectedMeal = mealOptions[selectedIndex];

return [{
    json: {
        selectedMeal: selectedMeal,
        originalNotionData: selectedMeal.originalNotionData,
        chatId: $json.callback_query.message.chat.id,
        messageId: $json.callback_query.message.message_id
    }
}];
