// Grab the JSON from "Setting Variables" Node
const configData = $node["Setting Variables"].json;

// Create our Valid Commands Array
const validCommands = [
  configData.command_ai_chat,           // "chat"
  configData.command_websearch_ai_chat, // "search"
  configData.command_deep_research,     // "deepsearch"
  configData.command_compose,           // "compose"
  configData.command_imagine,           // "imagine"
  configData.command_introduction,      // "introduction"
  configData.command_sales_pitch        // "sales pitch"
];

// Get the command prefix
const prefix = configData.command_prefix; // "/"

// Get the message from the incoming JSON
const fullMessage = $node["Unipile - On New Message"].json.body.message;

// Extract the command from the message
const command = fullMessage.startsWith(prefix) 
  ? fullMessage.split(" ")[0].substring(prefix.length) // Remove prefix, e.g., "chat" from "/chat"
  : "";

// Check if the command matches any valid value and split the message
if (validCommands.includes(command)) {
  // Extract the message content after the command
  const messageContent = fullMessage.substring(prefix.length + command.length).trim();
  return [{ json: { command: command, message: messageContent } }];
} else {
  return []; // No output if the command doesn‚Äôt match
}

