// Get all photos
const allItems = $input.all();
const referenceImages = []; // Lifestyle photos (with or without labubus)
const products = [];        // Our labubu products to add/replace

// Separate reference images and products by folder ID
let foundReferenceEnd = false;
for (const item of allItems) {
  // Check if this is a product by looking at parent folder
  if (item.json.parents && item.json.parents.includes('1yxVQyDsafpJuT7N2gx8O6Vbm1uKgb8zw')) {
    products.push(item.json);
    foundReferenceEnd = true;
  } else if (!foundReferenceEnd || (item.json.parents && item.json.parents.includes('1EErMLWL2z2IKKo_30UtpTkZyMBoA1ikW'))) {
    referenceImages.push(item.json);
  }
}

// If separation didn't work, use position-based split
if (referenceImages.length === 0 || products.length === 0) {
  referenceImages.length = 0;
  products.length = 0;
  const referenceItems = $('List Reference Images1').all();
  const productItems = $('List Product Photos').all();

  referenceImages.push(...referenceItems.map(item => item.json));
  products.push(...productItems.map(item => item.json));
}

console.log(`Found ${referenceImages.length} lifestyle reference images and ${products.length} labubu products`);

// Create exactly 15 combinations
const combinations = [];
let combinationCount = 0;
const targetCombinations = 15;

// Generate combinations with metadata
for (let i = 0; i < referenceImages.length && combinationCount < targetCombinations; i++) {
  for (let j = 0; j < products.length && combinationCount < targetCombinations; j++) {
    const reference = referenceImages[i];
    const product = products[j];

    combinations.push({
      combinationId: `combo_${String(combinationCount + 1).padStart(2, '0')}`,
      combinationIndex: combinationCount,
      totalCombinations: targetCombinations,
      referenceImageId: reference.id,
      referenceImageName: reference.name,
      productId: product.id,
      productName: product.name,
      imageType: 'lifestyle_labubu_integration',
      processingType: 'auto_detect', // Will be determined by image analysis
      createdAt: new Date().toISOString(),
      metadata: {
        referenceIndex: i,
        productIndex: j,
        batchInfo: `Batch 1 of 1 (${targetCombinations} items)`
      }
    });

    combinationCount++;
  }
}

console.log(`Created ${combinations.length} combinations for processing`);
console.log(`Each will be analyzed to determine if labubu replacement or addition is needed`);

// Return all combinations as separate items
return combinations.map(combo => ({ json: combo }));

// Get the combination data and downloaded images
const combinationData = $('Loop Over Combinations1').item.json;
const referenceDownload = $('Download Reference Image1').item;
const productDownload = $('Download Product Image1').item;

// Check if downloads were successful
let referenceBinary = null;
let productBinary = null;
let downloadStatus = 'success';
let downloadErrors = [];

// Handle reference image (lifestyle photo with existing labubu)
if (referenceDownload.binary && referenceDownload.binary.referenceImage) {
  referenceBinary = referenceDownload.binary.referenceImage;
} else {
  downloadErrors.push('Failed to download reference lifestyle image');
  downloadStatus = 'partial_failure';
}

// Handle product image (our labubu to replace with)
if (productDownload.binary && productDownload.binary.productImage) {
  productBinary = productDownload.binary.productImage;
} else {
  downloadErrors.push('Failed to download product image');
  downloadStatus = 'partial_failure';
}

// If both failed, mark as complete failure
if (!referenceBinary && !productBinary) {
  downloadStatus = 'failed';
}

// Prepare the complete data package
const preparedData = {
  // Combination info
  combinationData: {
    ...combinationData,
    downloadStatus,
    downloadErrors
  },

  // Processing metadata
  processingMetadata: {
    combinationId: combinationData.combinationId,
    referenceImageName: combinationData.referenceImageName,
    productName: combinationData.productName,
    imageType: combinationData.imageType,
    downloadedAt: new Date().toISOString(),
    readyForAI: downloadStatus === 'success',
    currentIndex: combinationData.combinationIndex + 1,
    totalCombinations: combinationData.totalCombinations
  }
};

// Log progress
console.log(`Processing ${preparedData.processingMetadata.currentIndex} of ${preparedData.processingMetadata.totalCombinations}`);
console.log(`Reference: ${preparedData.processingMetadata.referenceImageName}, Product: ${preparedData.processingMetadata.productName}`);

// Add binary data to output if available
const output = { json: preparedData };

if (referenceBinary || productBinary) {
  output.binary = {};

  if (referenceBinary) {
    output.binary.referenceImage = referenceBinary;
  }

  if (productBinary) {
    output.binary.productImage = productBinary;
  }
}

return output;

// Collect all saved images from the execution
const allSaved = $('Save Generated Image').all();

const summary = {
  totalCombinations: 15,
  successfulGenerations: allSaved.length,
  successRate: ((allSaved.length / 15) * 100).toFixed(1) + '%',
  startTime: $('Create All Combinations').first().json.createdAt,
  endTime: new Date().toISOString(),
  savedImages: allSaved.map(item => ({
    combinationId: item.json.combinationId,
    fileId: item.json.id,
    fileName: item.json.name
  }))
};

return summary;

