// CONFIGURATION SECTION - Customize these for your product
const PRODUCT_NAME = "product"; // e.g., "charm", "accessory", "pin"
const TARGET_ITEM = "target item"; // e.g., "bag", "jacket", "phone case"
const ATTACHMENT_METHOD = "attached"; // e.g., "clipped", "pinned", "stuck"
const SIZE_SPECIFICATION = "appropriately sized"; // e.g., "15-20cm (60-70% of item width)"
const PLACEMENT_RULE = "must be attached to the item"; // Your specific rule

// Parse agent output
let agentOutput;
try {
  agentOutput = typeof $json.output === 'string' ? JSON.parse($json.output) : $json.output;
} catch (error) {
  console.error('Parse error:', error);
  agentOutput = {
    imageType: 'lifestyle_shot',
    requiredAction: 'add_to_target',
    targetDetails: { color: TARGET_ITEM }
  };
}

const originalData = $('Prepare Image Data1').item.json;
let generationPrompt;

// Extract details from analysis
const imageType = agentOutput.imageType;
const action = agentOutput.requiredAction;
const target = agentOutput.targetDetails || agentOutput.bagDetails || {};
const person = agentOutput.personDetails || {};

// Build prompts based on image type and required action
if (imageType === 'product_shot') {
  // Product shots - item only, no person
  switch(action) {
    case 'replace_existing':
      generationPrompt = `Using @reference as base, keep the ${target.color || ''} ${TARGET_ITEM} EXACTLY as shown. Replace ONLY the existing ${PRODUCT_NAME} with @product ${PRODUCT_NAME} in the EXACT same position. The ${PRODUCT_NAME} must remain ${ATTACHMENT_METHOD} to the ${TARGET_ITEM}. Do not change anything else.`;
      break;

    case 'add_new':
      generationPrompt = `Using @reference as base, keep the ${target.color || ''} ${TARGET_ITEM} EXACTLY as shown. ADD @product ${PRODUCT_NAME} ${ATTACHMENT_METHOD} to the ${TARGET_ITEM} at ${target.bestPlacementPoint || 'the most visible/appropriate location'}. The ${PRODUCT_NAME} should be ${SIZE_SPECIFICATION}. Everything else remains identical.`;
      break;
  }
} else {
  // Lifestyle shots - person visible with item
  switch(action) {
    case 'relocate_to_target':
      // Person holding product, must move to target item
      generationPrompt = `CRITICAL INSTRUCTION: In @reference, the person is holding a ${PRODUCT_NAME}. Create the EXACT same photo with these changes:

1. REMOVE the ${PRODUCT_NAME} from the person's hands
2. ATTACH the @product ${PRODUCT_NAME} TO THE ${target.color || ''} ${TARGET_ITEM} at ${target.bestPlacementPoint || 'an appropriate location'}
3. The person's hands must be EMPTY or in a natural position
4. The ${PRODUCT_NAME} ${PLACEMENT_RULE}

PRESERVE EXACTLY: The person's appearance, pose, expression, clothing, and the background. Only the ${PRODUCT_NAME} location changes.`;
      break;

    case 'replace_existing':
      generationPrompt = `Using @reference, keep EVERYTHING identical. Replace ONLY the ${PRODUCT_NAME} currently on the ${target.color || ''} ${TARGET_ITEM} with @product ${PRODUCT_NAME} in the SAME position. The ${PRODUCT_NAME} must remain ${ATTACHMENT_METHOD} to the ${TARGET_ITEM}.`;
      break;

    case 'add_new':
      generationPrompt = `Using @reference, keep EVERYTHING identical. ADD @product ${PRODUCT_NAME} to the ${target.color || ''} ${TARGET_ITEM} at ${target.bestPlacementPoint || 'an appropriate visible location'}. 

CRITICAL: The ${PRODUCT_NAME} ${PLACEMENT_RULE}. The person's current position must remain exactly the same.

Size: ${SIZE_SPECIFICATION}. Everything else remains unchanged.`;
      break;
  }
}

// Add universal requirements based on your product rules
generationPrompt += `

ABSOLUTE REQUIREMENT: The @product ${PRODUCT_NAME} ${PLACEMENT_RULE}. Follow the reference image exactly except for the specified ${PRODUCT_NAME} placement.

FINAL CHECK: The generated image should show the ${PRODUCT_NAME} ${ATTACHMENT_METHOD} to the ${target.color || ''} ${TARGET_ITEM}, while everything else remains identical to @reference.`;

// Add emphasis for specific scenarios (customize as needed)
if (person.currentlyHoldingProduct) {
  generationPrompt = `URGENT: RELOCATE ${PRODUCT_NAME.toUpperCase()}!\n\n${generationPrompt}\n\nREMINDER: The ${PRODUCT_NAME} must be moved from hands to ${TARGET_ITEM}.`;
}

// Return formatted data
return {
  json: {
    ...originalData,
    agentAnalysis: agentOutput,
    generationConfig: {
      prompt: generationPrompt,
      imageType: imageType,
      action: action,
      referenceTag: "reference",
      productTag: "product",
      criticalRule: PLACEMENT_RULE
    },
    debugInfo: {
      scenario: `${imageType} - ${action}`,
      productName: PRODUCT_NAME,
      targetItem: TARGET_ITEM,
      placementLocation: target.bestPlacementPoint || 'target location'
    }
  },
  binary: $binary
};

// Get the current combination data
const currentCombination = $json;
const loopData = $('Loop Over Combinations1').context;

// Check if we have loop context
if (!loopData) {
  throw new Error('Loop context not found');
}

// Get the current position in the loop
const currentIndex = loopData.currentRunIndex || 0;
const totalItems = loopData.maxRunIndex || 15;

// Log progress
console.log(`Completed combination ${currentIndex + 1} of ${totalItems}`);
console.log(`Just processed: ${currentCombination.referenceImageName} + ${currentCombination.productName}`);

// Pass through all data and add loop tracking
return {
  json: {
    ...currentCombination,
    loopProgress: {
      currentIndex: currentIndex + 1,
      totalItems: totalItems,
      percentComplete: ((currentIndex + 1) / totalItems * 100).toFixed(1) + '%',
      isLastItem: currentIndex + 1 === totalItems
    }
  }
};
