// This is the most fundamental way to get data in n8n.
// It retrieves the array of all incoming items, and we select the first one.
const firstItem = $input.all()[0];

// Now we can safely get the JSON data from that item.
const agentData = firstItem.json;

// This variable will hold our final array of scenes.
let scenesArray = [];

// The rest of this logic is correct and will now work.
if (agentData.output && agentData.output.scenes) {
  scenesArray = agentData.output.scenes;
} else if (agentData.scenes) {
  scenesArray = agentData.scenes;
}

// If we successfully found a scenes array, return it.
if (scenesArray && scenesArray.length > 0) {
  return scenesArray;
} else {
  // If no scenes were found, stop with a clear error.
  throw new Error("Could not find a 'scenes' array in the input data. Check the AI Agent output.");
}

// This code assumes it's receiving binary image data from the Download Image node

const items = $input.all();
const output = [];

items.forEach((item, index) => {
  const timestamp = Date.now();
  const filename = `temp_scene_${index}_${timestamp}.jpg`;
  const filepath = `/short_video_maker/images/${filename}`;

  output.push({
    json: {
      ...item.json,
      sceneIndex: index,
      imageFilename: filename,
      imagePath: filepath
    },
    binary: item.binary  // Ensure binary data is passed through for writing
  });
});

return output;

// Bridge Code1 - Simplified Version
// This node processes items coming from "Read/Write Files from Disk"
const allItems = $input.all();

// Validation
if (!allItems || allItems.length === 0) {
  throw new Error("No items received from Read/Write Files node");
}

// Get the original AI Agent output scenes
const originalAgentData = $('AI Agent').first().json.output;

// Validation for AI Agent data
if (!originalAgentData || !originalAgentData.scenes) {
  throw new Error("Could not find scenes data from AI Agent node");
}

console.log(`Processing ${allItems.length} items from file write operations`);

// Process each item that was written to disk
const outputItems = allItems.map((item, index) => {
  // Get the corresponding scene data from AI Agent
  const sceneData = originalAgentData.scenes[index];

  if (!sceneData) {
    throw new Error(`No scene data found for index ${index}`);
  }

  // Create output item with all necessary data (videoConfig removed)
  return {
    json: {
      text: sceneData.text,
      imagePrompt: sceneData.imagePrompt,
      sceneIndex: index,
      imagePath: item.json.imagePath,
      imageFilename: item.json.imageFilename,
    },
    // Pass through binary data if it exists
    ...(item.binary ? { binary: item.binary } : {})
  };
});

console.log(`Bridge Code1 processed ${outputItems.length} items successfully`);

return outputItems;

// N8N Node: "Format JSON2VIDEO Structure"
const allItems = $input.all();

if (!allItems || allItems.length === 0) {
  throw new Error("No items received from Bridge Code1");
}

// Get global variables from n8n (set these in your "Set Global Variables" node)
const globalVars = $('Set Global Variables1').item.json;
const voiceId = globalVars.ELEVENLABS_VOICE_ID || "2ajXGJNYBR0iNHpS4VZb"; // Default voice
const backgroundAudioUrl = globalVars.BACKGROUND_AUDIO_URL || ""; // Optional background music URL

// --- NEW ---
// Get the video title from the global variables node
const videoTitle = globalVars.TITLE || "Untitled_Video";
// --- END NEW ---

console.log(`Formatting ${allItems.length} scenes for JSON2VIDEO clone`);

// Build scenes array in JSON2VIDEO format
const scenes = [];

for (const [index, item] of allItems.entries()) {
  if (!item.json.imagePath) {
    console.warn(`Skipping scene ${index} - no image path`);
    continue;
  }
  scenes.push({
    imageData: item.json.imagePath,
    text: item.json.text,
    voiceOverText: item.json.text,
    imagePrompt: item.json.imagePrompt,
    overlaidText: item.json.text.substring(0, 50) + "...", // Shortened for overlay
    sceneIndex: index
  });
}

// Create JSON2VIDEO-compatible structure
const json2videoConfig = {
  // --- NEW ---
  // Add the title to the JSON object so the Python script can access it
  title: videoTitle,
  // --- END NEW ---
  id: "n8n_generated",
  comment: "Video created by n8n JSON2VIDEO clone",
  height: 1920,
  width: 1080,
  quality: "medium",
  draft: false,
  resolution: "instagram-story",
  fps: 25,
  cache: true,

  scenes: scenes,

  variables: {
    voice: voiceId,
    background_audio_url: backgroundAudioUrl,
    scenes: scenes.map(scene => ({
      overlaidText: scene.overlaidText,
      voiceOverText: scene.voiceOverText,
      imagePrompt: scene.imagePrompt
    }))
  },

  elements: [
    ...(backgroundAudioUrl ? [{
      id: "background-music",
      type: "audio",
      src: backgroundAudioUrl,
      duration: -2,
      "fade-out": 2,
      volume: 0.2
    }] : []),

    {
      id: "subtitles",
      type: "subtitles",
      settings: {
        "font-family": "Oswald Bold",
        "font-size": 140,
        "outline-color": "#000000",
        "outline-width": 8,
        "position": "mid-bottom-center"
      },
      language: "auto",
      comment: "Auto-generated subtitles"
    }
  ]
};

console.log(`JSON2VIDEO structure created with ${scenes.length} scenes`);
console.log(`Title: ${videoTitle}`); // Log the title for debugging
console.log(`Voice ID: ${voiceId}`);
console.log(`Background Audio: ${backgroundAudioUrl || 'None'}`);

// Return the formatted structure
return [{
  json: json2videoConfig
}];

// Get the output from the previous Execute Command node
const executeNodeOutput = items[0].json;
const stdout = executeNodeOutput.stdout;

// Find the starting position of the JSON object
// We use lastIndexOf('{') because the JSON is the last thing printed
const jsonStartIndex = stdout.lastIndexOf('{');

// Check if a JSON object was found
if (jsonStartIndex === -1) {
  throw new Error("Could not find a JSON object in the stdout from the previous node.");
}

// Extract the JSON string from the full stdout
const jsonString = stdout.substring(jsonStartIndex);

try {
  // Parse the extracted string into a usable JavaScript object
  const cleanData = JSON.parse(jsonString);

  // Return the clean data for the next node (e.g., Google Sheets)
  return [{
    json: cleanData
  }];
} catch (error) {
  console.error("Failed to parse JSON string:", jsonString);
  throw new Error("The extracted string could not be parsed as JSON. Check the execute node's output.");
}
