parameters.jsCode:
// This is the most fundamental way to get data in n8n.
// It retrieves the array of all incoming items, and we select the first one.
const firstItem = $input.all()[0];

// Now we can safely get the JSON data from that item.
const agentData = firstItem.json;

// This variable will hold our final array of scenes.
let scenesArray = [];

// The rest of this logic is correct and will now work.
if (agentData.output && agentData.output.scenes) {
  scenesArray = agentData.output.scenes;
} else if (agentData.scenes) {
  scenesArray = agentData.scenes;
}

// If we successfully found a scenes array, return it.
if (scenesArray && scenesArray.length > 0) {
  return scenesArray;
} else {
  // If no scenes were found, stop with a clear error.
  throw new Error("Could not find a 'scenes' array in the input data. Check the AI Agent output.");
}

// This code assumes it's receiving binary image data from the Download Image node

const items = $input.all();
const output = [];

items.forEach((item, index) => {
  const timestamp = Date.now();
  const filename = `temp_scene_${index}_${timestamp}.jpg`;
  const filepath = `/mnt/m2drive/docker-data/short_video_maker/images/${filename}`;

  output.push({
    json: {
      ...item.json,
      sceneIndex: index,
      imageFilename: filename,
      imagePath: filepath
    },
    binary: item.binary  // Ensure binary data is passed through for writing
  });
});

return output;

// Fixed Bridge Code - processes downloaded images and creates video configuration
const allItems = $input.all();

// Validation
if (!allItems || allItems.length === 0) {
  throw new Error("No items received from previous node");
}

// Get the original AI Agent output - correct way to access node data
const originalAgentData = $('AI Agent').first().json.output;

// Validation for AI Agent data
if (!originalAgentData || !originalAgentData.scenes) {
  throw new Error("Could not find scenes data from AI Agent node");
}

// Process each downloaded image item and preserve binary data
const outputItems = [];

allItems.forEach((item, index) => {
  if (originalAgentData.scenes && originalAgentData.scenes[index]) {
    const scene = originalAgentData.scenes[index];

    // Create NEW output item (don't modify existing item)
    const newItem = {
      json: {
        text: scene.text,
        imagePrompt: scene.imagePrompt,
        sceneIndex: index,
        // Include original headers info
        statusCode: item.json.statusCode,
        // Include the video config only on the first item
        ...(index === 0 ? {
          videoConfig: {
            scenes: originalAgentData.scenes.map((s, i) => ({
              text: s.text,
              imagePrompt: s.imagePrompt,
              sceneIndex: i
            })),
            config: originalAgentData.config || {
              music: "dark",
              voice: "bf_emma", 
              orientation: "portrait"
            }
          }
        } : {})
      }
    };

    // Only add binary if it exists
    if (item.binary) {
      newItem.binary = item.binary;
    }

    outputItems.push(newItem);
  }
});

console.log("Bridge code processed", outputItems.length, "items with binary data preserved");

// Return all items with preserved binary data
return outputItems;

// Collect all images with their binary data and create final video config
const allItems = $input.all();

// Get the video config from the first item
const videoConfig = allItems[0].json.videoConfig;
if (!videoConfig) {
  throw new Error("No video config found in first item");
}

// Process each image and convert to base64
const scenesWithImages = [];

allItems.forEach((item, index) => {
  // Convert binary to base64
  let base64Image = '';
  if (item.binary && item.binary.data) {
    base64Image = Buffer.from(item.binary.data).toString('base64');
  }

  scenesWithImages.push({
    text: item.json.text,
    imagePrompt: item.json.imagePrompt,
    imageData: `data:image/jpeg;base64,${base64Image}`,
    sceneIndex: item.json.sceneIndex
  });
});

// Create final video configuration
const finalVideoConfig = {
  scenes: scenesWithImages,
  config: videoConfig.config
};

console.log(`Processed ${scenesWithImages.length} scenes with images`);

// Return single item with complete video config
return [{
  json: {
    output: finalVideoConfig
  }
}];
